settings.custom_liquid:
{% assign today = 'now' | date: "%d %b" %}
{% assign dispatched = 'now' | date: "%s" | plus: 86400 | date: "%d %b" %}
{% assign delivered_start = 'now' | date: "%s" | plus: 172800 | date: "%a" %}
{% assign delivered_end = 'now' | date: "%s" | plus: 345600 | date: "%a" %}

<div style="max-width: 700px; margin: auto; font-family: sans-serif;">
  <h2 style="text-align: center; font-weight: 700;">ORDER TIMELINE</h2>
  <div style="
    display: flex;
    justify-content: space-between;
    gap: 30px;
    background-color: #f7f7f7;
    padding-top: 20px;
    padding-bottom: 20px;
    border-radius: 12px;
    box-shadow: 0 0 5px rgba(0,0,0,0.1);
    flex-wrap: wrap;
  ">

    <div style="flex: 1; text-align: center;">
      <div style="font-weight: 800; font-size: 12px;">ORDERED</div>
      <div style="color: #555; font-size: 13px; font-weight: 600;">TODAY</div>
    </div>

    <div style="flex: 1; text-align: center;">
      <div style="font-weight: 800; font-size: 12px;">SHIPPED</div>
      <div style="color: #555; font-size: 13px; font-weight: 600;">TOMORROW</div>
    </div>

    <div style="flex: 1; text-align: center;">
      <div style="font-weight: 800; font-size: 12px;">DELIVERED</div>
      <div style="color: #555; font-size: 13px; font-weight: 600; text-transform: uppercase;">
        {{ delivered_start }} - {{ delivered_end }}
      </div>
    </div>

  </div>
</div>
